import renderWordCloudNative from 'wordcloud';
import { deepCopy } from "@/common/utility";
export default {
  name: "WordCloud",
  props: {
    data: {
      type: Array,
      required: true
    }
  },
  watch: {
    data: function data() {
      this.renderWordCloud();
    }
  },
  mounted: function mounted() {
    this.renderWordCloud();
  },
  updated: function updated() {
    this.renderWordCloud();
  },
  computed: {
    weightFactor: function weightFactor() {
      // magic number
      return 100.0 / this.data.reduce(function (previous, current) {
        return Math.max(previous, current[1]);
      }, 0);
    }
  },
  methods: {
    renderWordCloud: function renderWordCloud() {
      renderWordCloudNative(this.$el, {
        list: deepCopy(this.data),
        weightFactor: this.weightFactor,
        rotateRatio: 0
      });
    }
  }
};
